tutorialsIndex_pt = {"Básico", "Intermediário", "Avançado", "Habilidades", "Pesca", "Extra", "Missões", "Missões de Npc"}

tutorialsInfo_pt = {
    {
        {name = "01. Introdução", text = "1- O Zshop é onde você pode fazer compras para seu personagem ou pokémon\n\n2- Habilidade é onde você consegue ver todas as informações do seu personagem\n\n3- Sua mochila é onde você vai guardar seus pokémons e itens\n\n4- O controle de batalha é onde você pode gerenciar as ações de seu pokémon sobre os pokémons selvagens sendo as ações ( Ofensivo, Balanceado e Denfensivo ).\n\n5- Amigos é onde você gerencia a sua lista de amigos podendo adicionar, excluir, colocar icones para cada um e até mesmo ver quem está online no jogo.\n\n6- Batalha é onde você verá todos os pokémons selvagens, Npcs, personagens do jogo.\n\n 7- Mapa é onde você verá a sua localização no jogo, apertando CTRL + TAB você consegue ampliar este mapa.", img = "/images/game/Module/tutorial/basico/icones"}, 
        {name = "02. Manusear Itens", text = "Para manusear itens, apenas clique e segure com o botão esquerdo do mouse e arraste-o para onde quiser, você pode mover itens dentro ou fora do seu inventário, e também coletá-los ou joga-los fora.\n\nEntretanto existem itens únicos que não podem ser descartados.", img = "/images/game/Module/tutorial/basico/manuseandoitem"}, 
        {name = "03. Chamar Pokémon", text = "Para chamar seu Pokemon, Pokemon arraste-o para o menu pokémon, em seguida, clique no botão direito do seu mouse .", img = "/images/game/Module/tutorial/basico/chamaropokemon"}, 
        {name = "04. Atacando Inimigos", text = "Para atacar um inimigo, apenas clique em seu alvo (Botão direito do mouse), depois que o alvo for marcado, seu Pokémon vai atacar o inimigo automaticamente.", img = "/images/game/Module/tutorial/basico/atacarpokemon"},
        {name = "05. Usando ataques", text = "Os ataques de cada Pokémon são desbloqueados conforme você sobe de level.\nPara usar um ataque apenas clique em seu icone (Botão esquerdo do mouse) ou utilize os atalhos 'F1, F2, F3, F4....'. Os atalhos também podem ser redefinidos nas configurações de hotkey.", img = "/images/game/Module/tutorial/basico/atacandopokemon"},
        {name = "06. Loot de Pokémon", text = "Todo Pokémon depois de derrotado pode conter itens, para coletá-los, apenas clique no Pokémon caido (Botão direito do mouse).\nSe a opção de auto loot estiver habilitada, os itens vão ser coletados automaticamente, caso contrário, você deverá arrastar os itens que deseja para a sua mochila.", img = "/images/game/Module/tutorial/basico/lootpokemon"}, 
        {name = "07. Capturar Pokémon", text = "Para capturar um Pokémon, clique em uma de suas pokébolas vazias (Botão direito do mouse), depois use em um Pokémon derrotado (Botão esquerdo do mouse).\n\nSe o centro da Pokebola ficar verde, você capturou, se ficar vermelho, o Pokémon escapou!!", img = "/images/game/Module/tutorial/basico/capturandopokemon"}, 
        {name = "08. Mapa", text = "O mapa do jogo pode ser aberto com o atalho 'CTRL+M' ou por seu icone.\n\nO mapa mostra localizações importantes como cidades, centro Pokémon e lojas.\n\nPara visualizar o mapa inteiro, use 'CTRL+Tab'.", img = "/images/game/Module/tutorial/basico/mapa"},
		{name = "09. Loja", text = "Para abrir as ofertas da loja, diga (hi/trade) ao vendedor.\n\nNela você pode comprar Pokebolas, Poções, mochilas e revives.\n\nE também pode vender itens para o vendedor.", img = "/images/game/Module/tutorial/basico/loja"},        
        {name = "10. Pokedéx", text = "Para desbloquear dados de um novo Pokémon, basta clicar sobre o icone da Pokédex e depois usá-la no Pokémon que deseja coletar os dados(Botão esquerdo do Mouse).\n\nPara abrir a Pokedèx repita o mesmo processo, porém use-a em seu personagem.\n\nNa Pokedèx você poderá encontrar vários dados, como level, evolução, boost, stats, ataques, habilidades, efetividades e curiosidades.", img = "/images/game/Module/tutorial/basico/pokedex"},
        {name = "11. Depósito de Itens", text = "O depósito pode ser encontrado em qualquer centro Pokémon ou Loja, para abrir, apenas clique com o depósito (Botão direito do mouse).\nNo depósito você guarda quantos Pokémons ou Itens quiser.\n\nLembrando que você pode adquirir versões personalizadas do depósito para colocar em sua casa.", img = "/images/game/Module/tutorial/basico/lockers"},
        {name = "12. Escadas e Cordas", text = "Para subir escadas ou cordas, apenas clique com o botão direito do mouse na BASE da escada como indicado no Video.", img = "/images/game/Module/tutorial/basico/escadas"},
        {name = "13. Curando o Pokémon", text = "Para curar seus pokémons feridos, basta ir até um centro Pokémon e dizer 'hi' para a Nurse Joy.\n\nCurar o seu Pokémon não tem custo nenhum, mas caso queira curalos fora do centro pokémon, basca comprar na loja REVIVES.", img = "/images/game/Module/tutorial/basico/curandopokemon"},
    },
    {
        {name = "01. Olhar Objetos", text = "Para olhar algo, posicione o mouse e pressione os 2 botões do mouse ao mesmo tempo.\n\nOutra maneira de olhar algo é utilizando 'CTRL+Botão direito do mouse' e selecionando a opção 'olhar'.\n\nOlhar é muito útil para pegar informações de itens e Pokémons, principalmente para saber o nome do item ou Pokémon.", img = "/images/game/Module/tutorial/intermediario/olhandoobjetos"},
        {name = "02. Trocar Roupa", text = "Para trocar sua roupa, segure 'CTRL' e clique no seu personagem (Botão direito do mouse), depois selecione 'Escolher Roupa'.\n\nDica: Roupas básicas são vendidas pelo NPC Cilan em Cerulean.", img = "/images/game/Module/tutorial/intermediario/outfit"},
        {name = "03. Hotkeys", text = "Aqui voce pode adicionar os atalhos como: remédios e poções ou comandos com os movimentos da batalha de seu Pokemon.", img = "/images/game/Module/tutorial/intermediario/hotkey"},
        {name = "04. Evolução", text = "Para um Pokémon evoluir, você precisa ter determinado level ou você pode usar pedras de evolução em determinados Pokémons.\n\nFire stone, Water stone, Leaf stone, Punch Stone, Rock stone, Enigma stone, Venom Stone, Darkness stone, Hearth stone, Earth stone, Ice stone, Metal stone, Thunder stone, Feather stone, Shiny stone.\n\nEssas pedras de evolução você pode obtelas dropando de pokémons ou comprando de outros jogadores.", img = "/images/game/Module/tutorial/intermediario/evoluindo"},
        {name = "05. Lista de amigos", text = "A lista de amigos mostra se seus amigos estão 'Online' ou 'Offline' no jogo, além de fornecer várias opções como mensagens privadas, Editar amigo, podendo colocar um icone e uma descrição ao seu amigo, e também a opção de ignorar o jogador.\n\nPara adicionar um amigo é muito fácil, apenas clique com o botão direito na lista, selecione a opção de adicionar amigo, E digite o nome do jogador que deseja adicionar como amigo.\n\nOutro meio de adicionar alguém, é segurando 'Ctrl' e clicando no jogador que deseja adicionar, depois selecionar a opção 'Adicionar a lista VIP'.", img = "/images/game/Module/tutorial/intermediario/amigos"},
    },	
    {
        {name = "01. Troca", text = "Para fazer uma troca, segure (Ctrl) e clique com o botão direito do mouse sobre o item ou Pokémon que você deseja trocar e selecione (Trocar Com..) e selecione o Jogador que você quer fazer a troca. (Se for vários itens e Pokémons, coloque-os em uma Mochila)", img = "/images/game/Module/tutorial/intermediario/troca"},
        {name = "02. Casa", text = "Para comprar uma casa, você precisa ser jogador 'VIP' e estar no nível 50+.n\nPara comprar uma casa, ela precisa estar sem dono, veja o preço da casa dando 'OLHANDO' em sua porta, depois só ir em frente a porta da house e dizer !buyhouse\n\nA cada mês será cobrado o preço da casa, em seu depósito, caso contrário, você perderá sua casa.", img = "/images/game/Module/tutorial/intermediario/casa"},
        {name = "03. Canais de conversa", text = "1. Private Chat Channel 'Chat privado'.\n\n2. Help 'Canal de ajuda para jogadores onde os tutores ajudaram em qualquer tipo de duvidas'.\n\n3. Game Chat Br 'Canal de conversa GLOBAL Brasileiro'.\n\n4. Game Chat EN 'Canal de conversa GLOBAL Americano'.\n\n5. Game Chat ES 'Canal de conversa GLOBAL Espanhol'.\n\n6. Trade 'Canal de anuncios e vendas GLOBAL'.", img = "/images/game/Module/tutorial/intermediario/chat"},
		{name = "04. Direcionar Pokémon", text = "Para mudar a direção do seu pokémon é muito simples e útil duelos e caças\n.Apenas digite ou coloque em sua Hotkey os seguintes comandos seguir:\n\nNorte: a1\nEste: a2\nSul: a3\nOeste: a4", img = "/images/game/Module/tutorial/intermediario/direcionar"},
    },	
    {
        {name = "01. Mover", text = "Para movimentar o seu Pokémon, abra a janela Pokémon e clica no icone 'Ordem' e depois clique no chão, seu Pokémon irá até o local escolhido.\n\nEste comando é de vital importância em duelos de alto nivel, por isso é recomendado utilizar uma hotkey para isso. O hotkey padrão do 'Ordem' é o botão scroll do mouse 'botão do meio', apenas clicando com o scroll no chão, seu Pokémon irá até o local ordenado.", img = "/images/game/Module/tutorial/habilidades/mover"},
        {name = "02. Corte", text = "Para cortar arbustos, apenas ordene um Pokémon com a habilidade 'CUT' no arbusto.", img = "/images/game/Module/tutorial/habilidades/cut"}, 
        {name = "03. Dig", text = "Para abrir buracos fechados, Apenas ordene um Pokémon com a habilidade 'DIG' no buraco fechado.", img = "/images/game/Module/tutorial/habilidades/dig"},
        {name = "04. Light", text = "Para usar a luz, apenas a ordena um Pokémon com a habilidade 'LIGHT'.", img = "/images/game/Module/tutorial/habilidades/light"},
        {name = "05. Rock Smash", text = "Para quebrar pedras, apenas ordene um Pokémon com a habilidade 'ROCK SMASH' na pedra.", img = "/images/game/Module/tutorial/habilidades/rock smash"},
		{name = "06. Ride", text = "Para montar em um Pokémon, apenas ordene um Pokémon com a habilidade 'RIDE' em seu treinador.\n\nPara desmontar, repita o mesmo processo.", img = "/images/game/Module/tutorial/habilidades/ride"},
        {name = "07. Surf", text = "Para nadar, apenas ande até uma borda de aguá que automaticamente você irá surfar com o seu Pokémon, Para sair repita o mesmo processo.\n\nEsta habilidade funciona apenas com Pokémons com a habilidade 'SURF'.", img = "/images/game/Module/tutorial/habilidades/surf"},
        {name = "08. Fly", text = "Para voar em um Pokémon, apenas ordene um Pokémon com fly em seu treinador.\n\nPara mudar de altura, use o comando 'h1' para subir e 'h2' para descer.\n\nPara desmontar, repita o mesmo processo estando em solo.", img = "/images/game/Module/tutorial/habilidades/fly"},
        {name = "09. Teleport", text = "Para teleportar para alguma cidade você precisa de um Pokémon com Teleport. Depois abra o seu mapa com o atalho: (CTRL+TAB).\nE escolha a cidade que você deseja Teleportar, Como o video a cima.\n\nOBS: Somente jogadores VIPS podem se teleportar, caso queira teleportar para outras cidades em todos os centros pokémons tem um painel de teleport que será cobrado uma taxa.", img = "/images/game/Module/tutorial/habilidades/teleport"},
        {name = "10. Blink", text = "Quando um Pokémon tem blink ou dark portal e você ordena que ele ande até algum local, ele automaticamente usará o blink.", img = "/images/game/Module/tutorial/habilidades/blink"},
    },	
    {
        {name = "01. Como Funciona", text = "Para pescar, você vai precisar de uma vara de pesca, Podendo conseguir uma 'VARA' na cidade inicial, Pallet.\n\nApós ter feito a missão entregue pelo NPC em pallet ou ter comprado uma 'VARA' use algum lago ou mar Clicando com o botão direito na 'VARA' e o botão esquerdo no mar.\n\nQuando o Pokémon Morder a isca ele aparecerá ao seu lado, como no video a cima.\n\nNovos Pokémons aparecem dependendo da isca que você utilizar e o seu level de pesca.\nLembrando que cada vara tem sua isca exclusiva.\n\nOBS: Sempre que você atingir o level máximo de pesca de determinada 'VARA' você terá que fazer um aprimoramento na sua vara ou comprar de outros jogadores para então voltar a subir o nivel de sua pesca.\n\nDICA: Iscas podem ser compradas com o NPC Guru.", img = "/images/game/Module/tutorial/pesca/funciona"},
        {name = "02. Como Melhorar", text = "Para fazer uma melhoria em sua vara você precisará ir até o porto de Pallet. Lá você vai encontrar o NPC 'Siegfried' e ao lado dele você encontrará um computador onde clicando com o 'Botão direito do Mouse' irá abrir uma janela de Melhoria.", img = "/images/game/Module/tutorial/pesca/localizacao"},
        {name = "03. Vara Comum", text = "A Vara Comum é a vara inicial, ganhada por um NPC na quest localizada na rota de Pallet.\nÉ com ela que você irá começar a sua jornada e subirá seus primeiros leveis de pesca.\n\nAs iscas Exclusivas desta vara são:\nMinhoca\n    Pokémons pescados: Poliwag, Goldeen, Horsea.\nAlgas Marinhas\n    Pokémons pescados: Tentacool, Staryu, Krabby, Shellder.\n\nPara fabricar esta vara você vai precisar dos seguintes itens:\nCompressed Seed (50)\nSeavel Essence (01)\nWater Gem (250)\nWater Stone (01)\nWater Pendant (15)\nWool Ball (05)", img = "/images/game/Module/tutorial/pesca/varacomum"},
        {name = "04. Vara de Metal", text = "A Vara de Metal é a vara intermediaria, Por ser uma melhoria da 'Vara Comum' Ela tem capacidade de pescar Pokémons mais poderosos.\n\nAs iscas Exclusivas desta vara são:\nAlgas Marinhas Nutridas\n    Pokémons pescados: Seel, Psyduck, Slowpoke, Seaking.\nCamarões\n    Pokémons pescados: Shellder, Slowpoke, Poliwhirl.\nBife\n    Pokémons pescados: Poliwhirl, Golduck, Dewgong.\n\nPara fabricar esta vara você vai precisar dos seguintes itens:\nCompressed Seed (75)\nSeavel Essences (05)\nWater Gem (450)\nWater Stone (02)\nWater Pendant (50)\nPiece of Steel (50)\nWool Ball (05)\nVara Comum (01)", img = "/images/game/Module/tutorial/pesca/varademetal"}, 
        {name = "05. Vara Mecânica", text = "A Vara Mecânica é uma vara capaz de puxar os Pokémons mais rapidamente.\n\nAs iscas Exclusivas desta vara são:\nBife Grande\n    Pokémons pescados: Cloyster, Kingler, Seadra.\nSardinha\n    Pokémons pescados: Staryu, Cloyster, Slowbro.\n\nPara fabricar esta vara você vai precisar dos seguintes itens:\nSmall Stones (600)\nSeavel Essences (05)\nWater Gem (450)\nWater Stone (02)\nWater Pendant (50)\nCompressed Steel (25)\nBee Sting (15)\nWool Ball (05)\nElectric Sphere (10)\nVara de Metal (01)", img = "/images/game/Module/tutorial/pesca/varamecanica"},
        {name = "06. Vara Mecânica Prof..", text = "A Vara Mecânica Profissional tem a mesma função da sua antecessora, porem ela tem o poder de pescar Pokémons mais poderosos.\n\nAs iscas Exclusivas desta vara são:\nSardinha Temperada\n    Pokémons pescados: Starmie, Poliwrath, Squirtle.\nMisty's Special Lure\n    Pokémons pescados: Dratini, Dragonair, Dragonite.\n\nPara fabricar esta vara você vai precisar dos seguintes itens:\nBug Essences (100)\nSeavel Essences (05)\nGem Star (75)\nWater Stone (02)\nSardinha Temperada (100)\nCompressed Steel (25)\nIronhard Essences (15)\nWool Ball (10)\nElectric Sphere (50)\nVara Mecânica (01)", img = "/images/game/Module/tutorial/pesca/varamecanicaprofissional"},
        {name = "07. Caixa de Pandora", text = "A Caixa de Pandora é a melhor de todas, além de pescar Pokémons ultra raros ela tem a capacidade de utilizar as iscas de suas antecessoras.\n\nAs iscas Exclusivas desta vara são:\nAtração Sonora 50%\n    Pokémons pescados: Wartortle, Tentacruel, Gyarados, Omanyte.\nAtração Sonora 100%\n    Pokémons pescados: Gyarados, Vaporeon, Lapras, Omastar, Kabutops, Blastoise.\n\nPara fabricar esta vara você vai precisar dos seguintes itens:\nSeavel Essences (10)\nMalefic Essences (10)\nRaibolt Essences (10)\nWater Stone (25)\nSolid Water Gem (50)\nGiant Dragon Scales (50)\nCompressed Steel (100)\nVara de Metal (01)\nVara Mecânica (01)\nVara Mecânica Profissional (01)", img = "/images/game/Module/tutorial/pesca/caixadepandora"},
    },		
    {
        {name = "01. Saffari", text = "A Zona Safari está localizada em Fuchsia á cima do Centro Pokémon. É um local onde você poderá encontrar raros Pokémons. Para entrar no Saffari você precisará de 15k.\nPara entrar faça os seguintes dialogos:\n'hi' 'saffari' 'yes'\n\nVocê pode ficar quanto tempo quiser dentro do Saffari.\nOBS: Dentro do Saffari é somente a Saffari ball irá funcionar.\nE não se esqueça que se Morrer lá dentro você perderá experiência e Bless.", img = "/images/game/Module/tutorial/extra/saffari"},
        {name = "02. Addons", text = "Addons são roupas para Pokémons. Você pode encontrá-las na 'LojaZ' no 'Shopping' de Celadon ou fabricando nas profissões.", img = "/images/game/Module/tutorial/extra/addon"},
        {name = "03. Taunt", text = "Taunt é uma animação que muitas outfits podem utilizar. A maioria das outfits compradas na 'LojaZ' tem taunt.\nVocê também pode adquirir roupas com taunt no shopping de celadon.\n\nO comando para usar o taunt é ( !taunt ).\n\nOBS: as outfits iniciais possuem a animação de taunt.", img = "/images/game/Module/tutorial/extra/taunt"},
        {name = "04. Cassino", text = "O Cassino fica na cidade de Celadon, nele você pode encontrar as caça niques.", img = "/images/game/Module/tutorial/extra/cassino"},
    },			
    {
        {name = "01. Rock Stone", text = "Level Minimo: 60 a 100\n\nUm pesquisador encontrou uma caverna repleta de Pokémons dos tipos (Pedra e Terra) ele relatou que no final da caverna teria um baú mas não arriscou em ir verificar pois teria muitos Onixs e Rhydons protegendo o local.\n\nOBS: Para chegar ao bau dessa missão você precisará de um Pokémon que possua a habilidade 'SURF'.", img = "/images/game/Module/tutorial/missoes/rockstone"},
        {name = "02. Missão de Cerulean", text = "Level Minimo: 50 a 70\n\nOs moradores da cidade de Cerulean decidiram criar um pequeno desafio para os treinadores iniciantes, então juntaram alguns Pokémons na caverna da praia de cerulean e colocaram um baú no final. Quem será o treinador que conquistará esse prêmio ?\n\nOBS: Para chegar ao bau dessa missão você precisará de um Pokémon que possua a habilidade 'Rock Smash'.", img = "/images/game/Module/tutorial/missoes/ceruleanquest"},
        {name = "03. Venom Stone", text = "Level Minimo: 70 a 100\n\nUm treinador da cidade de Cerulean relatou que estava caçando Arboks e achou um baú misterioso, Oque será que está escondido por lá ?\n\nOBS: Para chegar ao bau dessa missão você precisará de um Pokémon que possua a habilidade 'Dig'.", img = "/images/game/Module/tutorial/missoes/venomstone"},
        {name = "04. Missão de Saffron", text = "Level Minimo: 35 a 60\n\nOs moradores da cidade de Saffron decidiram criar um pequeno desafio para os treinadores intermediários, então foram até os boeiros de saffron e esconderam um baú com um prêmio misterioso.", img = "/images/game/Module/tutorial/missoes/saffronquest"},
        {name = "05. Fire Stone", text = "Level Minimo: 100 a 200\n\nMoltres está guardando um baú misterioso nas profundesas da caverna de Cinnabar.\n\nOBS: Para chegar ao bau dessa missão você precisará de um Pokémon que possua a habilidade 'Dig'.", img = "/images/game/Module/tutorial/missoes/firestonequest"},
        {name = "06. Crystal Stone", text = "Level Minimo: 100 a 200\n\nArticuno está guardando um baú misterioso nas profundesas da caverna de ICE ISLAND.", img = "/images/game/Module/tutorial/missoes/crystalstone"},
        {name = "07. Jungle Island", text = "Level Minimo: 60 a 100\n\nUm bando de Scythers estão guardando um baú misterioso na Jungle island.\n\nOBS: Para chegar ao bau dessa missão você precisará de um Pokémon que possua a habilidade 'Surf'.", img = "/images/game/Module/tutorial/missoes/jungleisland"},
        {name = "08. Darknes Stone", text = "Level Minimo: 80 a 120\n\nUm bando de Gengars estão guardando um baú misterioso em Lavender.\n\nOBS: Para chegar ao bau dessa missão você precisará de um Pokémon que possua a habilidade 'Cut'.", img = "/images/game/Module/tutorial/missoes/darknessstone"},
    },			
    {
        {name = "01. Pikachu Sequestrado", text = "Level Minimo: 1 a 10\n\nParece que Pikachu foi sequestrado pela equipe rockets, corra para resgata-lo.\n\nPara aceitar a missão do NPC siga os comandos:\n'hi' 'pikachu'", img = "/images/game/Module/tutorial/missoesnpc/rocketsinicio"},
        {name = "02. Box 1", text = "Level Minimo: 5 a 20\n\nProfessor carvalho ganhou um ovo Pokémon de um treinador que estava de passagem porem para ele não será útil então ele dará para quem for capaz de cumprir com seu pedido.\n\nOBS: Para entregar a missão para o NPC você precisará mostrar para ele o Pokémon pedido.", img = "/images/game/Module/tutorial/missoesnpc/box1"},
        {name = "03. Vara Inicial", text = "Level Minimo: 10 a 20\n\nPatrick é um pescador muito habilidoso e está na rota de Pallet para ajudar os treinadores iniciantes a conseguir pegar o seu Primeiro Magikarp\n\nPara aceitar a missão do NPC siga os comandos:\n'hi' 'mission' 'yes'", img = "/images/game/Module/tutorial/missoesnpc/varainicial"},
        {name = "04. Ajude Flora", text = "Level Minimo: 50 a 80\n\nFlora está sem nada para fazer no Museu de Pewter, então está entregando um desafio para cada treinador que passa por ali.\n\nPara aceitar a missão do NPC siga os comandos:\n'hi' 'mission' 'yes'", img = "/images/game/Module/tutorial/missoesnpc/flora"},
        {name = "05. Ajude Rolland", text = "Level Minimo: 10 a 30\n\nRolland está de passagem por Cerulean e está precisando da ajuda de algum treinador para pegar alguns itens.\n\nPara aceitar a missão do NPC siga os comandos:\n'hi' 'mission' 'yes'", img = "/images/game/Module/tutorial/missoesnpc/rolland"},
        {name = "06. Ajude Adriano", text = "Level Minimo: 70 a 100\n\nAdriano está em cinnabar caçando charizards e precisa de alguém para te ajudar a juntar alguns itens para fabricar um bracelete flamejante.\n\nPara aceitar a missão do NPC siga os comandos:\n'hi' 'mission' 'yes'", img = "/images/game/Module/tutorial/missoesnpc/adriano"},
        {name = "07. Ajude Hugo", text = "Level Minimo: 70 a 100\n\nHugo veio para lavender para caçar, porém ficou com medo dos gengars e haunters e decidiu desafiar os treinadores que passam por ali.\n\nPara aceitar a missão do NPC siga os comandos:\n'hi' 'mission' 'yes'", img = "/images/game/Module/tutorial/missoesnpc/hugo"},
        {name = "08. Ajude Clara", text = "Level Minimo: 12 a 25\n\nClara está na praça de saffron pedindo a ajuda de algum treinador para pegar alguns itens.\nEla precisa muito desses itens para fazer uma poção de cura.\n\nPara aceitar a missão do NPC siga os comandos:\n'hi' 'mission' 'yes'", img = "/images/game/Module/tutorial/missoesnpc/clara"},
    },	
}